'use strict';

// MODULES //

var filled = require( '@stdlib/array/base/filled' );
var assign = require( './assign.js' );


// MAIN //

/**
* Cumulatively tests whether at least one element in an array passes a test implemented by a predicate function, while iterating from right-to-left.
*
* @param {Array} x - input array
* @param {Function} predicate - function which tests array elements
* @returns {Array} output array
*
* @example
* var x = [ 0, 1, 0, 0, 0 ];
* function isPositive(value) {
*     return (value > 0);
* }
*
* var y = cuanyByRight(x, isPositive);
* // returns [ false, false, false, true, true ]
*/
function cuanyByRight(x, predicate) {
    var y = filled(false, x.length);
    return assign(x, y, 1, 0, predicate);
}


// EXPORTS //

module.exports = cuanyByRight;
